def search_user_data(data, key):
    if 'users' in data:
        for user in data['users']:
            if 'user' in user and key in user['user']:
                return user['user'][key]
            # If the key is in a nested structure
            if 'emails' in user['user']:
                for email in user['user']['emails']:
                    if key == 'email':
                        return email['email']
    return None  # Return None if the key is not found

